def PROCESSING_DateToggle = context.expand( '${#TestCase#PROCESSING_DateToggle}' )
int dateToggle = Integer.parseInt(PROCESSING_DateToggle)
Date date = new Date()+(dateToggle*30)
String dateFormat = "yyyy-MM-dd'T'HH:mm:ssXXX"
java.util.Calendar c = Calendar.getInstance()
c.setTime(date)
int dayOfWeek = c.get(Calendar.DAY_OF_WEEK)
String lastDate = context.expand( '${#TestCase#GENERATED_DueDate}' )


if(dateToggle==3)
{
	//Get last date string and convert it into date object
	dateString = lastDate.replaceAll("\"", "")
	String[] splitString = dateString.split("T")
	date = Date.parse("yyyy-MM-dd", splitString[0])
	date=date-1;
}

if(dayOfWeek == Calendar.SATURDAY)
{
	date=date-1;
}
else if(dayOfWeek == Calendar.SUNDAY)
{
	date=date-2;
}


date.set(hourOfDay: 11, minute: 59, second: 0)
def dateString = date.format(dateFormat)

// Can be removed as date also includes time
if(dateToggle == 1)
{
	testRunner.testCase.setPropertyValue("PROCESSING_DateToggle", "2");
}else
{
	testRunner.testCase.setPropertyValue("PROCESSING_DateToggle", "1");
}

testRunner.testCase.testSteps["Run Validations"].setPropertyValue("EXPECTED_dueDate", dateString);
dateString = /"/ + dateString + /"/
testRunner.testCase.setPropertyValue("GENERATED_DueDate", dateString)

def status = context.expand( '${Status Holder#Status}' )
if(status.equals("530")){
	testRunner.testCase.setPropertyValue("INPUT_DateToggle", "3")
	testRunner.gotoStepByName("Generate Date")
}

import com.eviware.soapui.SoapUI
import com.eviware.soapui.settings.HttpSettings
import com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus

// get the settings 
def settings = SoapUI.getSettings();
// save the possible previous timeout
def bk_timeout = settings.getString(HttpSettings.SOCKET_TIMEOUT,"");
// set the new timeout... in your case 5 minutes in miliseconds
settings.setString(HttpSettings.SOCKET_TIMEOUT,"300000");
// save the new settings 
SoapUI.saveSettings();

// get the testStep by name
def testStep = testRunner.testCase.getTestStepByName('PUT Stage')
// run it
def result = testStep.run( testRunner, context )
if( result.status == TestStepStatus.OK )
{
    // ... if all ok    
}

// when finish set the timeout to default value again
settings.setString(HttpSettings.SOCKET_TIMEOUT, bk_timeout)
SoapUI.saveSettings()

def status = testRunner.testCase.testSteps["PUT Stage"].testRequest.response.responseHeaders["#status#"] // returns an array of one value
testRunner.testCase.testSteps["Run Validations- Error Response"].setPropertyValue("INCOMING_HTTPStatus", status[0].trim());
//testRunner.testCase.testSteps["Run Validations-PUT"].setPropertyValue("INCOMING_HTTPStatus", status[0].trim());

Date date = new Date()+2
String dateFormat = "YYYY-MM-dd"
java.util.Calendar c = Calendar.getInstance()
c.setTime(date)

//String lastDate = context.expand( '${#TestCase#GENERATED_DueDate}' )


def dateString = date.format(dateFormat)
dateString += "T11:59:00+12:00"
log.info dateString


// Can be removed as date also includes time


//testRunner.testCase.testSteps["Run Validations"].setPropertyValue("EXPECTED_dueDate", dateString);
dateString = /"/ + dateString + /"/
testRunner.testCase.setPropertyValue("GENERATED_DueDate", dateString)

def PROCESSING_DateToggle = context.expand( '${#TestCase#PROCESSING_DateToggle}' )
int dateToggle = Integer.parseInt(PROCESSING_DateToggle)
Date date = new Date()+(dateToggle*30)
String dateFormat = "YYYY-MM-dd"
java.util.Calendar c = Calendar.getInstance()
c.setTime(date)
int dayOfWeek = c.get(Calendar.DAY_OF_WEEK)
String lastDate = context.expand( '${#TestCase#GENERATED_DueDate}' )


if(dateToggle==3)
{
	//Get last date string and convert it into date object
	dateString = lastDate.replaceAll("\"", "")
	log.info dateString
	String[] splitString = dateString.split("T")
	log.info(splitString[0])
	date = Date.parse("yyyy-MM-dd", splitString[0])
	log.info(date)
	date=date-1;
}

if(dayOfWeek == 7)
{
	date=date-1;
}
else if(dayOfWeek == 1)
{
	if(dateToggle==3)
	{
		date=date-2;
	}
	else
	{
		date=date+1;
	}
}

def dateString = date.format(dateFormat)
dateString += "T11:59:00+12:00"
log.info dateString


// Can be removed as date also includes time
if(dateToggle == 1)
{
	testRunner.testCase.setPropertyValue("PROCESSING_DateToggle", "2");
}else
{
	testRunner.testCase.setPropertyValue("PROCESSING_DateToggle", "1");
}

testRunner.testCase.testSteps["Run Validations"].setPropertyValue("EXPECTED_dueDate", dateString);
dateString = /"/ + dateString + /"/
testRunner.testCase.setPropertyValue("GENERATED_DueDate", dateString)

def status = context.expand( '${Status Holder#Status}' )
if(status.equals("530")){
	testRunner.testCase.setPropertyValue("PROCESSING_DateToggle", "3")
	testRunner.gotoStepByName("Date Generator")
}

