{
	//Get last date string and convert it into date object
	dateString = lastDate.replaceAll("\"", "")
	log.info dateString
	String[] splitString = dateString.split("T")
	log.info(splitString[0])
	date = Date.parse("yyyy-MM-dd", splitString[0])
	log.info(date)
