def validationCheck = [count:0, message:""]

def INPUT_HTTP_VERSION = context.expand( '${#Project#INPUT_HTTP_VERSION}' )

def EXPECTED_soType = context.expand( '${#TestCase#EXPECTED_soType}' )
def EXPECTED_soStage = context.expand( '${#TestCase#EXPECTED_soStage}' )
def EXPECTED_salesOrder = context.expand( '${#TestCase#EXPECTED_salesOrder}' )
def EXPECTED_dueDate = context.expand( '${#TestCase#EXPECTED_dueDate}' )
def EXPECTED_completedBy = context.expand( '${#TestCase#EXPECTED_completedBy}' )
def EXPECTED_customerName = context.expand( '${#TestCase#EXPECTED_customerName}' )
def EXPECTED_createDate = context.expand( '${#TestCase#EXPECTED_createDate}' )


def INCOMING_HTTPStatus = context.expand( '${#TestCase#INCOMING_HTTPStatus}' )
def EXPECTED_HTTPStatus = context.expand( '${#TestCase#EXPECTED_HTTPStatus}' )

validationCheck = j.compareStrings(INCOMING_HTTPStatus, INPUT_HTTP_VERSION + " " +EXPECTED_HTTPStatus, validationCheck)


java.util.Map<String, Closure, String>[] orderDetails = 	[
													[node:"root", check:{slurpResult}, expected:null],
													[node:"soType", check:{slurpResult.soType}, expected:EXPECTED_soType],
													[node:"soStage", check:{slurpResult.soStage}, expected:EXPECTED_soStage],
													[node:"salesOrder", check:{slurpResult.salesOrder}, expected:EXPECTED_salesOrder],
													[node:"dueDate", check:{slurpResult.dueDate}, expected:EXPECTED_dueDate],
													[node:"completedBy", check:{slurpResult.completedBy}, expected:EXPECTED_completedBy],
													[node:"customerName", check:{slurpResult.customerName}, expected:EXPECTED_customerName],
													[node:"createDate", check:{slurpResult.createDate}, expected:EXPECTED_createDate]													
												]


for(int i = 1; i < orderDetails.length; i++)
{
	log.info "Node: " + orderDetails[i].node
	if(orderDetails[i].get('expected')!="")
	{
		validationCheck = j.checkJSONValue(slurpResult, orderDetails[i].get('node'), orderDetails[0].get('check'), orderDetails[i].get('check'),orderDetails[i].get('expected'), validationCheck)
	}
	else
	{
		validationCheck = j.checkJSONNodeExists(slurpResult, orderDetails[i].get('node'), orderDetails[0].get('check'), orderDetails[i].get('check'),false , validationCheck)
	}
}


def errorCount = validationCheck.count
def errorLog = validationCheck.message
log.info " Total number of failed assertion(s) is $errorCount"
log.info " Failed assertions: $errorLog"
assert errorCount == 0, "Errors found in assertions: $errorLog"

