def validationCheck = [count:0, message:""]

def INPUT_HTTP_VERSION = context.expand( '${#Project#INPUT_HTTP_VERSION}' )
def INCOMING_OrderID = context.expand( '${#TestCase#INCOMING_OrderID}' )
def INCOMING_HTTPStatus = context.expand( '${#TestCase#INCOMING_HTTPStatus}' )
def EXPECTED_HTTPStatus = context.expand( '${#TestCase#EXPECTED_HTTPStatus}' )
def expectedString = INPUT_HTTP_VERSION + " " + EXPECTED_HTTPStatus
def authCheck = INPUT_HTTP_VERSION + " " + statusCheck.CODE_401

if(INCOMING_HTTPStatus.contains(authCheck))
{	
	/*
	* Compares if the TestSuite name contain API10. If so concatenates the endpoint
	*with events rather than order/orderID
	*/
	if(TestSuiteName.contains("API10"))
	{
		def endpoint = context.expand( '${#Project#TEST_Endpoint}' )
		def resource = "/events"
		def authString = statusCheck.getFullAuthenticationString(endpoint,resource)
		String[] list = [authString, authCheck]
		validationCheck = j.compareStringToList(INCOMING_HTTPStatus, list, validationCheck)
	}
	else
	{
		def endpoint = context.expand( '${#Project#TEST_Endpoint}' )
		def resource = "/orders/" + INCOMING_OrderID
		def authString = statusCheck.getFullAuthenticationString(endpoint,resource)
		String[] list = [authString, authCheck]
		validationCheck = j.compareStringToList(INCOMING_HTTPStatus, list, validationCheck)
	}
}
else
{
	validationCheck = j.compareStrings(INCOMING_HTTPStatus, expectedString, validationCheck)
}


def errorCount = validationCheck.count
def errorLog = validationCheck.message
log.info " Total number of failed assertion(s) is $errorCount"
log.info " Failed assertions: $errorLog"
assert errorCount == 0, "Errors found in assertions: $errorLog"

