	context.testCase.getTestStepByName("Transfer EXPECTED Results").disabled=true
}
else
{
	context.testCase.getTestStepByName("GET Structured Work Instructions").disabled=false
	context.testCase.getTestStepByName("Transfer EXPECTED Results").disabled=false
}

