//2D Array of all address values and their relevant xPath. Separate from other values as this allows us to see the structure of the address
String[][] address = 	[
					[EXPECTED_address_id,"//address/id",1],
					[EXPECTED_streetNumConcat,"//address/streetNumConcat",1],
					[EXPECTED_roadNameConcat,"//address/roadNameConcat",1],
					[EXPECTED_addressName,"//address/addressName",0],
					[EXPECTED_localityName,"//address/localityName",0],
