					]

def INCOMING_HTTPStatus = context.expand( '${#TestCase#INCOMING_HTTPStatus}' )
def EXPECTED_HTTPStatus = context.expand( '${#TestCase#EXPECTED_HTTPStatus}' )

validationCheck = x.compareStrings(INCOMING_HTTPStatus, INPUT_HTTP_VERSION + " " +EXPECTED_HTTPStatus, validationCheck)

// Check everything expect address values via looping over the relevant 2D array. If the value is provided, do a comparison. Else, ensure it's not in the response.
for(int i = 0; i < expectedValues.length; i++)
{
	if(expectedValues[i][0]!=""&&expectedValues[i][0]!="<VALUE/>")
	{
		validationCheck = x.checkXpathValue(responseXml, expectedValues[i][1], expectedValues[i][0], validationCheck,true)
	}
	else
	{
		validationCheck = x.checkXpathNodeCount(responseXml, expectedValues[i][1], "0", validationCheck,true)
	}
}

// Check address values via looping over the relevant 2D array. If the value is provided, do a comparison. Else, ensure it's not in the response.
for(int i = 0; i < address.length; i++)
{
	if(address[i][0]!="")
	{
		validationCheck = x.checkXpathValue(responseXml, address[i][1], address[i][0], validationCheck,true)
	}
	else
	{
		validationCheck = x.checkXpathNodeCount(responseXml, address[i][1], "0", validationCheck,true)
	}
}


def errorCount = validationCheck.count
def errorLog = validationCheck.message
log.info " Total number of failed assertion(s) is $errorCount"
log.info " Failed assertions: $errorLog"
assert errorCount == 0, "Errors found in assertions: $errorLog"

