log.info "EXPECTED STATUS CODE OF INITIAL GET::::::::"+Expected_StatusCode_BeforeUpdate
log.info "EXPECTED STATUS CODE OF FINAL GET::::::::"+Expected_StatusCode_AfterUpdate

String Expected_MemoString

switch(Place)
{	
	case "top": Expected_MemoString=Request_DateTime + "\n" + MEMO_UpdateString + "\n\n" + MEMO_BeforePUT;
			  log.info "::::::::::Since Place parameter is passed as top, concatenating string at the top ::::::::::"
			  break;

	case "bottom": Expected_MemoString=MEMO_BeforePUT + "\n\n" + Request_DateTime + "\n" + MEMO_UpdateString;
				log.info "::::::::::Since Place parameter is passed as bottom, concatenating string at the End ::::::::::"
				break;
	case "":	Expected_MemoString=MEMO_BeforePUT + "\n\n" + Request_DateTime + "\n" + MEMO_UpdateString;
			log.info "::::::::::Since no Place parameter is passed, concatenating string at the End ::::::::::"
			break;	
}
def validationcheck = [count:0, message:""]

if(MEMO_UpdateString.contains("\\n"))
{
	String expectedString = Expected_MemoString.replaceAll("\\\\n","")
	expectedString = expectedString.replaceAll("\\n","")

	String actualString = MEMO_AfterPUT.replaceAll("\\n","")
	actualString = MEMO_AfterPUT.replaceAll("\n","")

	log.info("***************INSIDE THE IF CONDITION***********************")
	log.info "Expected Memo String has been formed : " + expectedString
	log.info "Actual Memo String has been obtained : " + actualString

	//Validating the status code of the get method done before and after the PUT request
	validationcheck=j.compareStrings(Incoming_StatusCode_BeforeUpdate,Expected_StatusCode_BeforeUpdate,validationcheck)
	validationcheck=j.compareStrings(Incoming_StatusCode_AfterUpdate,Expected_StatusCode_AfterUpdate,validationcheck)

	//Validating the expected and the actual memo string are equal
	validationcheck=j.compareStrings(actualString, expectedString, validationcheck)
}
else
{
	//loggin the expected memo string that has been formulated and the actual memo string returned in the get request
	log.info "Expected Memo String has been formed : " + expectedString
	log.info "Actual Memo String has been obtained : " + actualString

	//Validating the status code of the get method done before and after the PUT request

	validationcheck=j.compareStrings(Incoming_StatusCode_BeforeUpdate,Expected_StatusCode_BeforeUpdate,validationcheck)
	validationcheck=j.compareStrings(Incoming_StatusCode_AfterUpdate,Expected_StatusCode_AfterUpdate,validationcheck)

	//Validating the expected and the actual memo string are equal
	validationcheck=j.compareStrings(actualString, Expected_MemoString, validationcheck)
}


def error_count=validationcheck.count
def error_message=validationcheck.message

log.info "Number of error counts = "+error_count

if (error_count > 0)
{
	log.info "Error messages : "+error_message
}

assert error_count==0,"Error found while comparing the expected Memo string and The Actual Memo string for API008::::" + error_message

