import org.json.*;
import groovy.time.TimeCategory;
import java.text.SimpleDateFormat;
import org.apache.commons.lang.time.DateUtils;


j=new JSONUtilities()

def Actual_Status=context.expand('${#TestCase#INCOMING_Status}')
String Expected_Status=context.expand('${#TestCase#EXPECTED_Status}')
def IsNotificationPresentinResponse= context.expand('${#TestCase#IsNotificationPresentinResponse}')

//Checking if the status is 200.if not then does't move forward
if(!Actual_Status.equals(Expected_Status))
{
	//log.info "expected Status::::::::"+Expected_Status
	assert false,"The Status code for Get Events wasn't "+Expected_Status
}

//Fetching response and order id from the custom properties 
def Response= context.expand('${#TestCase#INCOMING_Response}')
String Expected_OrderId= context.expand('${#TestCase#Expected_OrderId}')
def EXPECTED_soType=context.expand('${#TestCase#EXPECTED_soType}')
def EXPECTED_soStage=context.expand('${#TestCase#EXPECTED_soStage}')
def EXPECTED_eventAction=context.expand('${#TestCase#EXPECTED_eventAction}')
def EXPECTED_ASID=context.expand('${#TestCase#EXPECTED_ASID}')
def NotificationInterval=context.expand('${#TestCase#NotificationInterval}')

if(!NotificationInterval)
{
	assert false,"The interval vaule isn't provided by user and is null"
}

log.info "Response recieved from the Custom Property::::::::"+Response
log.info "Expected Order Id recieved from Custom Property::::::::"+Expected_OrderId
log.info "Notification Interval is expected to be::::::::"+NotificationInterval

def mappedResult=j.slurpStringMulti(Response)

// Saving the '+' sign which will be used to concatenate later on
String plusSign= "\u002B";

//Converting it into negative to subtract it from the date
NegateNotificationInterval="-"+NotificationInterval
DateMinusNotificationInterval=DateUtils.addHours(new Date(),NegateNotificationInterval.toInteger())


for(i=0;i<mappedResult.size;i++)
{
	def DateSplit = mappedResult[i].crtDate.split('\\+')
	Actual_crtDate=DateSplit[0]+plusSign+"1200"
	ResponseDateTime=new Date().parse("yyyy-MM-dd'T'HH:mm:ss.SSSz",Actual_crtDate.trim())

	log.info "Actual Date:::::::"+ResponseDateTime
 	log.info "Expected Date Minus Interval:::::::"+DateMinusNotificationInterval

	if(ResponseDateTime<=DateMinusNotificationInterval)
	{
		assert false, "The date "+ResponseDateTime+" in the response notification is less than "+NotificationInterval+ " hours which is the selected interval by the user."
	}

}

import org.json.*;
import groovy.time.TimeCategory;
import java.text.SimpleDateFormat;

j = new JSONUtilities()

//Getting Custom properties
def Actual_Status=context.expand('${#TestCase#INCOMING_Status}')
String Expected_Status=context.expand('${#TestCase#EXPECTED_Status}')
def IsNotificationPresentinResponse= context.expand('${#TestCase#IsNotificationPresentinResponse}')

//Checking if the status is 200.if not then does't move forward
if(!Actual_Status.equals(Expected_Status))
{
	//log.info "expected Status::::::::"+Expected_Status
	assert false,"The Status code for Get Events wasn't "+Expected_Status
}

//Fetching response and order id from the custom properties 
def Response= context.expand('${#TestCase#INCOMING_Response}')
String Expected_OrderId= context.expand('${#TestCase#Expected_OrderId}')
def EXPECTED_soType=context.expand('${#TestCase#EXPECTED_soType}')
def EXPECTED_soStage=context.expand('${#TestCase#EXPECTED_soStage}')
def EXPECTED_eventAction=context.expand('${#TestCase#EXPECTED_eventAction}')
def EXPECTED_ASID=context.expand('${#TestCase#EXPECTED_ASID}')
def EXPECTED_crtDate=context.expand('${#TestCase#EXPECTED_crtDate}')

log.info "Response recieved from the Custom Property::::::::"+Response
log.info "Expected Order Id recieved from Custom Property::::::::"+Expected_OrderId

//converting the response into a map
def mappedResult = j.slurpStringMulti(Response)

/*Finds the block which contains the expected order id  and 
 saves the index of that that block into requiredIndex field.*/
def requiredIndex=0
def OrderPresentinNotif=0

// Saving the '+' sign which will be used to concatenate later on
String plusSign= "\u002B";

//Converting the date string to date object
EXPECTED_crtDate=new Date().parse("EEE, dd MMM yyyy hh:mm:ss z",EXPECTED_crtDate.trim())


for(i=0;i<mappedResult.size;i++)
{
	//Formatting the Actual date from UTC format to the same format as the Expected date.
	def DateSplit = mappedResult[i].crtDate.split('\\+')
	Actual_crtDate=DateSplit[0]+plusSign+"1200"
	GetDateTime=new Date().parse("yyyy-MM-dd'T'HH:mm:ss.SSSz",Actual_crtDate.trim())

	log.info "Expected Date::::::::"+EXPECTED_crtDate
	log.info "Actual Date after formatting::::::::"+GetDateTime
	//Adding a minute to the expected date
	use( TimeCategory ) {
	    after1Min = EXPECTED_crtDate + 1.minutes
	}

	if (Expected_OrderId.equals(mappedResult[i].orderId) && (GetDateTime.after(EXPECTED_crtDate) && GetDateTime.before(after1Min)))
	{
		requiredIndex=i
		OrderPresentinNotif=1
		break;
	}
}

//If the expected order id exists in the response then execute the if part
if(OrderPresentinNotif!=0)
{
	if(!IsNotificationPresentinResponse.equalsIgnoreCase("Yes"))
	{
		assert false,"The IsNotificationPresent flag is set to "+IsNotificationPresentinResponse+" but still a notification is present in the response."
	}
	//Saves all the actual values obtained in the response to various parameters
	def Actual_orderId=mappedResult[requiredIndex].orderId
	def Actual_soType=mappedResult[requiredIndex].soType
	def Actual_soStage=mappedResult[requiredIndex].soStage
	def Actual_eventAction=mappedResult[requiredIndex].eventAction
	def Actual_ASID=mappedResult[requiredIndex].asid
	def Actual_crtDate=mappedResult[requiredIndex].crtDate

	//logging expected and actual values
	log.info "Actual Order ID recieved from MAP obtained::::::::"+Actual_orderId
	log.info "Expected SO Type recieved from Custom Property::::::::"+EXPECTED_soType
	log.info "Actual SO type from MAP obtained:::::::"+Actual_soType
	log.info "Expected SO Stage recieved from Custom Property::::::::"+EXPECTED_soStage
	log.info "Actual SO Stage from MAP obtained:::::::"+Actual_soStage
	log.info "Expected Evt Action recieved from Custom Property::::::::"+EXPECTED_eventAction
	log.info "Actual Evt Action from MAP obtained:::::::"+Actual_eventAction
	log.info "Expected ASID recieved from Custom Property::::::::"+EXPECTED_ASID
	log.info "Actual ASID from MAP obtained:::::::"+Actual_ASID
	log.info "Expected Created Date from MAP obtained:::::::"+EXPECTED_crtDate
	log.info "Actual Created Date from MAP obtained:::::::"+Actual_crtDate


	def validationCheck=[count:0, message:""]

	validationCheck=j.compareStrings(Actual_soType,EXPECTED_soType,validationCheck)
	validationCheck=j.compareStrings(Actual_soStage,EXPECTED_soStage,validationCheck)
	validationCheck=j.compareStrings(Actual_eventAction,EXPECTED_eventAction,validationCheck)
	validationCheck=j.compareStrings(Actual_ASID,EXPECTED_ASID,validationCheck)


	//Checking if the Actual date is within a minute of the expected date or equal to expected date
	if(GetDateTime.after(EXPECTED_crtDate) && GetDateTime.before(after1Min) )
	{
		log.info "The crtDate in the Get request within a minute after the PUT request"
		assert true
	}
	else
	{
		log.info "The date in the notification is not within a minute of the PUT request."
		assert false,"The date in the notification is not within a minute of the PUT request.";
	}

	//Log error count and error messages.
	def error_count = validationCheck.count
	def error_messages= validationCheck.message

	log.info " Total number of failed assertion(s) is $error_count"
	if (error_count > 0)
	{
		log.info "Error messages : "+error_messages
	}
	assert error_count == 0, "Errors found in assertions: $error_messages"
}
//If the expected Order id isn't present in the JSON response then execute else part
else
{
	if(IsNotificationPresentinResponse.equalsIgnoreCase("Yes"))
	{
		assert false,"The IsNotificationPresent flag is set to "+IsNotificationPresentinResponse+" but still there is no notification present in the response."
	}
	log.info "The JSON Response doesn't contain the Expected Order ID:::::::"+Expected_OrderId	
}

