	log.info "Since the Value for Is_Place_Paremter_Present is "+Place_Parameter_Present+", disabling the PUT Memo Without Place Parameter Request"
	testRunner.testCase.getTestStepByName("PUT Memo Without Place Parameter").disabled=true
	testRunner.testCase.getTestStepByName("Property Transfer without Place Parameter").disabled=true
	testRunner.testCase.getTestStepByName("PUT Memo").disabled=false
	testRunner.testCase.getTestStepByName("Property Transfer").disabled=false

}
else
{
	log.info "Since the Value for Is_Place_Paremter_Present is "+Place_Parameter_Present+", disabling the PUT Memo Request"
	testRunner.testCase.getTestStepByName("PUT Memo").disabled=true
	testRunner.testCase.getTestStepByName("Property Transfer").disabled=true
	testRunner.testCase.getTestStepByName("PUT Memo Without Place Parameter").disabled=false
	testRunner.testCase.getTestStepByName("Property Transfer without Place Parameter").disabled=false
}

