import java.util.regex.Pattern
import java.util.regex.Matcher

//Grab incoming response and split at spaces
def INCOMING_HarResponse = context.expand( '${#TestCase#INCOMING_HarResponse}' )

String statusCode;
Matcher matcher;

//Define regexes (\\d regers to any digit [0-9], ^ refers to the start of a string, and \$ refers to the end of the string)
Pattern statusPattern = Pattern.compile("HTTP/\\d.\\d \\d{3}")
matcher = statusPattern.matcher(INCOMING_HarResponse)
if(matcher.find())
{
	statusCode = matcher.group(0); 
}

testRunner.testCase.setPropertyValue("OUTPUT_HTTPStatus", statusCode);

def PROCESSING_DateToggle = context.expand( '${#TestCase#INPUT_DateToggle}' )
int dateToggle = Integer.parseInt(PROCESSING_DateToggle)
Date date = new Date()+(dateToggle*30)
String dateFormat = "yyyy-MM-dd'T'HH:mm:ssXXX"
java.util.Calendar c = Calendar.getInstance()
c.setTime(date)
int dayOfWeek = c.get(Calendar.DAY_OF_WEEK)
String lastDate = context.expand( '${#TestCase#INPUT_LastDate}' )

if(dateToggle==3)
{
	//Get last date string and convert it into date object
	dateString = lastDate.replaceAll("\"", "")
	String[] splitString = dateString.split("T")
	date = Date.parse("yyyy-MM-dd", splitString[0])
	date=date-1;
}

if(dayOfWeek == Calendar.SATURDAY)
{
	date=date-1;
}
else if(dayOfWeek == Calendar.SUNDAY)
{
	date=date-2;
}


date.set(hourOfDay: 11, minute: 59, second: 0)
def dateString = date.format(dateFormat)

// Can be removed as date also includes time
if(dateToggle == 1)
{
	testRunner.testCase.setPropertyValue("INPUT_DateToggle", "2");
}else
{
	testRunner.testCase.setPropertyValue("INPUT_DateToggle", "1");
}

testRunner.testCase.setPropertyValue("OUTPUT_GeneratedDate", dateString);

