def INPUT_dueDate = context.expand( '${#TestCase#INPUT_dueDate}' )

if(INPUT_dueDate.equals("")||INPUT_dueDate==null)
{
	testRunner.testCase.getTestStepByName("Generate Date").disabled=false
	testRunner.testCase.getTestStepByName("Transfer Generated Date").disabled=false
}
else
{
	testRunner.testCase.getTestStepByName("Generate Date").disabled=true
	testRunner.testCase.getTestStepByName("Transfer Generated Date").disabled=true
}

def INPUT_DateToggle = context.expand( '${Generate Date#INPUT_DateToggle}' )
def OUTPUT_GeneratedDate = context.expand( '${Generate Date#OUTPUT_GeneratedDate}' )
int dateToggle = Integer.parseInt(INPUT_DateToggle)

testRunner.testCase.setPropertyValue("INPUT_LastDate",OUTPUT_GeneratedDate)

if(dateToggle==1)
{
	dateToggle = 2
}else if(dateToggle==2)
{
	dateToggle = 1
}

testRunner.testCase.setPropertyValue("INPUT_DateToggle",dateToggle+"")

def OUTPUT_HTTPStatus = context.expand( '${Extract Status#OUTPUT_HTTPStatus}' )
def OUTPUT_GeneratedDate = context.expand( '${#TestCase#INPUT_dueDate}' )
def response = context.expand( '${PUT DueDate#Response}' )
def messageBody = context.expand( '${#TestCase#INPUT_messageBody}' )
log.info messageBody
log.info response
//def INPUT_LastDate = context.expand( '${Generate Date#INPUT_LastDate}' )


if(OUTPUT_HTTPStatus.contains("530")&&response.contains("Insufficient Work Units")){
	testRunner.testCase.setPropertyValue("INPUT_DateToggle", "3")
	testRunner.gotoStepByName("Generate Date")
}
else if(OUTPUT_HTTPStatus.contains("200"))
{
	testRunner.testCase.setPropertyValue("INPUT_DateToggle", "1")
	testRunner.testCase.setPropertyValue("OUTPUT_GeneratedDate", OUTPUT_GeneratedDate)
}
